<style lang="scss" scoped type="text/scss">
  @import "~variables";

  .btn {
    margin-right: 5px;
    margin-bottom: 5px;
  }
</style>

<template>
  <div>
    <div class="padding align-center">
      <h2>OSS文件上传</h2>
    </div>

    <div class="padding bg-white">
      <h5>上传本地文件</h5>
      <div class="border-top margin-top padding-top">
        <input type="file" @change="upload" class="btn"/>
      </div>
    </div>


    <div class="padding bg-white margin-top-l">
      <h5>上传base64文件</h5>
      <div class="border-top margin-top padding-top">
        <button type="button" class="btn btn-info" @click="uploadBase64">上传base64</button>
      </div>
    </div>
  </div>
</template>

<script>
  import ossUploader from "scripts/oss-uploader";

  export default {
    name: 'ui-selector',
    methods: {
      upload: function (e) {
        let that = this;
        let files = e.target.files || e.dataTransfer.files;
        console.log(files[0]);
        if (!files.length) {
          return;
        }
        //console.log(files[0]);

        ossUploader.upload(files[0], "/vue-test", function (progress) {
          console.log("上传进度：" + progress);
        })
          .then((file) => {
            console.log("上传文件存储相对路径：" + file.filePath);
          });
      },

      uploadBase64: function () {
        const avatar = "data:image/jpeg;base64,";
        ossUploader.uploadBase64(avatar, "/vue-base64", function (progress) {
          console.log("上传进度：" + progress);
        })
          .then((file) => {
            console.log("上传文件存储相对路径：" + file.filePath);
            console.log("绝对路径：http://assets.xohaa.net" + file.filePath);
            console.log("文件对象" + file.fileObj);
          });
      }
    }
  }
</script>
